import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './KakaoMap.css';

const KakaoMap = ({ center, isSpotAdding, markers, setMarkers }) => {
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const [map, setMap] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchBarVisible, setIsSearchBarVisible] = useState(false);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=${process.env.REACT_APP_KAKAO_APP_API_KEY}&autoload=false&libraries=services,clusterer,places`;
    script.async = true;

    script.onload = () => {
      if (window.kakao && window.kakao.maps) {
        window.kakao.maps.load(() => setIsMapLoaded(true));
      } else {
        console.error('Ïπ¥Ïπ¥Ïò§ Îßµ Ïä§ÌÅ¨Î¶ΩÌä∏ Î°úÎî© Ïã§Ìå®');
      }
    };
    document.head.appendChild(script);
    return () => document.head.removeChild(script);
  }, []);

  useEffect(() => {
    if (isMapLoaded) {
      const mapContainer = document.getElementById('map');
      const mapOption = {
        center: new window.kakao.maps.LatLng(center?.lat || 37.2838, center?.lng || 127.0454),
        level: 5,
        draggable: true,
        scrollwheel: false,
        disableDoubleClickZoom: true,
        keyboardShortcuts: false,
        minLevel: 3,
        maxLevel: 7,
      };
      const createdMap = new window.kakao.maps.Map(mapContainer, mapOption);
      setMap(createdMap);

      // Spot Ï∂îÍ∞Ä Î™®Îìú ÏÑ§Ï†ï
      if (isSpotAdding) {
        window.kakao.maps.event.addListener(createdMap, 'click', (mouseEvent) => {
          const latlng = mouseEvent.latLng;
          const newMarker = { lat: latlng.getLat(), lng: latlng.getLng() };
          setMarkers((prevMarkers) => [...prevMarkers, newMarker]);

          const marker = new window.kakao.maps.Marker({ position: latlng, map: createdMap });
          window.kakao.maps.event.addListener(marker, 'click', () => {
            marker.setMap(null);
            setMarkers((prevMarkers) =>
              prevMarkers.filter((m) => m.lat !== newMarker.lat || m.lng !== newMarker.lng)
            );
          });
        });
      }

      // Í∏∞Ï°¥ ÎßàÏª§ Ï∂îÍ∞Ä
      markers.forEach((marker) => {
        const markerPosition = new window.kakao.maps.LatLng(marker.lat, marker.lng);
        new window.kakao.maps.Marker({
          map: createdMap,
          position: markerPosition,
        });
      });

      // Í≤ΩÎ°ú ÌëúÏãú
      if (markers.length > 1) {
        for (let i = 0; i < markers.length - 1; i++) {
          const start = markers[i];
          const end = markers[i + 1];
          getWalkingRouteFromTmap(start, end, createdMap);
        }
      }
    }
  }, [isMapLoaded, markers, isSpotAdding, center]);

  const getWalkingRouteFromTmap = (start, end, map) => {
    const url = `https://apis.openapi.sk.com/tmap/routes/pedestrian?version=1&format=json`;

    axios
      .post(
        url,
        {
          startX: start.lng,
          startY: start.lat,
          endX: end.lng,
          endY: end.lat,
          reqCoordType: 'WGS84GEO',
          resCoordType: 'WGS84GEO',
          startName: 'Ï∂úÎ∞úÏßÄ',
          endName: 'ÎèÑÏ∞©ÏßÄ',
        },
        {
          headers: {
            appKey: process.env.REACT_APP_TMAP_API_KEY,
          },
        }
      )
      .then((response) => {
        const resultData = response.data.features;
        const points = [];

        resultData.forEach((feature) => {
          if (feature.geometry.type === 'LineString') {
            feature.geometry.coordinates.forEach((coordinate) => {
              points.push(new window.kakao.maps.LatLng(coordinate[1], coordinate[0]));
            });
          }
        });

        if (points.length > 0) {
          const polyline = new window.kakao.maps.Polyline({
            path: points,
            strokeWeight: 5,
            strokeColor: '#FF0000',
            strokeOpacity: 0.7,
            strokeStyle: 'solid',
          });
          polyline.setMap(map);
        } else {
          console.warn('TmapÏóêÏÑú Í≤ΩÎ°ú Îç∞Ïù¥ÌÑ∞Î•º Í∞ÄÏ†∏Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§.');
        }
      })
      .catch((error) => {
        console.error('Tmap ÎèÑÎ≥¥ Í≤ΩÎ°ú ÏöîÏ≤≠Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§:', error);
      });
  };

  // Í≤ÄÏÉâ Í∏∞Îä• Íµ¨ÌòÑ
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchQuery.trim() !== '') {
      searchPlaces(searchQuery);
    }
  };

  const searchPlaces = (keyword) => {
    const ps = new window.kakao.maps.services.Places();

    ps.keywordSearch(keyword, (data, status) => {
      if (status === window.kakao.maps.services.Status.OK) {
        setSearchResults(data); // Í≤ÄÏÉâ Í≤∞Í≥º Ï†ÄÏû•
      } else if (status === window.kakao.maps.services.Status.ZERO_RESULT) {
        alert('Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§.');
        setSearchResults([]);
      } else {
        alert('Í≤ÄÏÉâ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        setSearchResults([]);
      }
    });
  };

  const handleResultClick = (place) => {
    const newCenter = new window.kakao.maps.LatLng(place.y, place.x);
    map.setCenter(newCenter);

    // Í≤ÄÏÉâ Í≤∞Í≥º Î™©Î°ù Ïà®Í∏∞Í∏∞
    setSearchResults([]);
    setIsSearchBarVisible(false);
  };

  return (
    <div style={{ position: 'relative', width: '100%', height: '400px' }}>
      {/* ÎèãÎ≥¥Í∏∞ ÏïÑÏù¥ÏΩò */}
      <button
        className="map-search-button"
        onClick={() => setIsSearchBarVisible(!isSearchBarVisible)}
      >
        üîç
      </button>

      {/* Í≤ÄÏÉâÎ∞î */}
      {isSearchBarVisible && (
        <form onSubmit={handleSearchSubmit} className="map-search-bar">
          <input
            type="text"
            value={searchQuery}
            onChange={handleSearchChange}
            placeholder="Ïû•ÏÜåÎ•º Í≤ÄÏÉâÌïòÏÑ∏Ïöî"
          />
          <button type="submit">Í≤ÄÏÉâ</button>
        </form>
      )}

      {/* Í≤ÄÏÉâ Í≤∞Í≥º Î™©Î°ù */}
      {isSearchBarVisible && searchResults.length > 0 && (
        <ul className="map-search-results">
          {searchResults.map((place, index) => (
            <li
              key={index}
              className="map-search-result-item"
              onClick={() => handleResultClick(place)}
            >
              {place.place_name}
            </li>
          ))}
        </ul>
      )}

      {/* Îßµ Ïª®ÌÖåÏù¥ÎÑà */}
      <div id="map" style={{ width: '100%', height: '100%' }}></div>
    </div>
  );
};

export default KakaoMap;
